import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import PVP from './PVP'
import PVC from './PVC'
import reportWebVitals from './reportWebVitals';


class Display extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: null
    }

    this.setOption = this.setOption.bind(this);
    this.pageChoice = this.pageChoice.bind(this);
  }

  setOption(value) {
    this.setState({page: value});
  }

  pageChoice() {
    if(this.state.page === 1) {
      return <App />
    }
    if(this.state.page === 2) {
      return <PVP />
    }
    if(this.state.page === 3) {
      return <PVC />
    }
    return;
  }

  render() {
    const content = this.pageChoice;
    return(
      <div>
        <button onClick={() => this.setOption(1)}>Learn about handSigns</button>
        <button onClick={() => this.setOption(2)}>Play against a friend</button>
        <button onClick={() => this.setOption(3)}>Play against the computer</button>
        <div>{content}</div>
      </div>
    )
  };
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Display />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
