import React from 'react';

function Handsigns() {
    return(<PVP />);
}

function HandSign(props) {
    return(
        <button className="object" onClick={props.onClick}>
            {props.value}
        </button>
    );
}

function FetchButton(props) {
    return(
        <button className="fetch" onClick={props.onClick}>
            "Play"
        </button>
    );
}

class PVP extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            p1Turn: true,
            p1Choice: null,
            p2Choice: null,
            objects: ['air','airplane','alien','axe','baby','beer','bicycle','bird','blood','book',
                    'bowl','brain','butter','cage','camera','car','castle','cat','chain','chainsaw',
                    'church','cloud','cockroach','community','computer','cross','cup','death','devil',
                    'diamond','dragon','duck','dynamite','electricity','fence','film','fire','fish',
                    'gold','grass','guitar','gun','heart','helicopter','home','king','laser','law',
                    'lightning','man','math','medusa','money','monkey','moon','mountain','noise','nuke',
                    'paper','peace','pit','planet','platimum','poison','police','porcupine','power','prayer',
                    'prince','princess','queen','quicksand','rain','rainbow','robot','rock','satan','school',
                    'scissors','sky','snake','spider','sponge','sun','sword','tv','tank','toilet','tornado',
                    'train','tree','turnip','ufo','vampire','videogame','vulture','wall','water','whip','wolf',
                    'woman']
        };

        this.getHandSigns = this.getHandSigns.bind(this);
        this.handleSignSelection = this.handleSignSelection.bind(this);
    }

    getHandSigns() {
        if (this.state.objects != null) {
            return;
        }
        let url = "https://rps101.pythonanywhere.com/api/v1/objects/all"
        fetch(url)
        .then((response) => {
            return(response.json());
        }).then((signs) => {
            console.log(signs);
            this.setState({objects:signs});
            console.log(this.state.objects);
        });
    }

    handleSignSelection(event) {
        if(this.state.p1Turn) {
            this.setState({p1Choice: event.target.value});
        } else {
            this.setState({p2Choice: event.target.value});
        }
        this.setState({p1Turn: !this.state.p1Turn})
    }

    fetchResults() {
    if (this.state.p1Choice === null || this.state.p1Choice === null) {
        return null;
    }
    let url = "https://rps101.pythonanywhere.com/api/v1/match?object_one=" + props.p1Choice + "&object_two=" + props.p2Choice;
    fetch(url)
    .then((results) => {
        return(results.json());
    }).then((outcome) => {
        return outcome;
    });
}

    render() {
        fetchResults();
        //console.log(results);
        const signs = this.state.objects.map((signName) => {
            <HandSign value={signName}
                onClick={this.handleSignSelection} />;
        }); 

        return(
            <div>
                {signs}
            </div>
        );
    }
}

export default Handsigns;
